function rowRotate(th) {
	const thead = th.parentNode.parentNode.parentNode.children[0].children[0];
	const cols = thead.children.length - 1;
	if (cols > 1) {
		const tr = th.parentNode;
		if (th.getAttribute('rowspan')) {
			th.removeAttribute('rowspan');
			const fragment = document.createDocumentFragment();
			const ntd = document.createElement('td');
			ntd.classList.add('bg-body');
			if (tr.children[2].classList.contains('text-secondary')) {
				ntd.classList.add('text-secondary');
			}
			ntd.style.maxWidth = '75vw';
			const npre = document.createElement('div');
			npre.classList.add('mb-0');
			npre.classList.add('overflow-auto');
			npre.style.maxHeight = '20vh';
			npre.style.whiteSpace = 'pre';
			npre.appendChild(document.createTextNode(tr.children[2].innerText));
			ntd.appendChild(npre);
			fragment.appendChild(ntd);
			tr.removeChild(tr.children[2]);
			tr.removeChild(tr.children[1]);
			for (let i=1; i<cols; i++) {
				const ntd = document.createElement('td');
				ntd.classList.add('bg-body');
				if (tr.nextSibling.children[1].classList.contains('text-secondary')) {
					ntd.classList.add('text-secondary');
				}
				ntd.style.maxWidth = '75vw';
				const npre = document.createElement('div');
				npre.classList.add('mb-0');
				npre.classList.add('overflow-auto');
				npre.style.maxHeight = '20vh';
				npre.style.whiteSpace = 'pre';
				npre.appendChild(document.createTextNode(tr.nextSibling.children[1].innerText));
				ntd.appendChild(npre);
				fragment.appendChild(ntd);
				tr.parentNode.removeChild(tr.nextSibling);
			}
			tr.appendChild(fragment);
		} else {
			const fragment = document.createDocumentFragment();
			for (let i=1; i<cols; i++) {
				const ntr = document.createElement('tr');
				fragment.appendChild(ntr);
				const nth = document.createElement('th');
				nth.classList.add('bg-body-tertiary');
				nth.appendChild(document.createTextNode(thead.children[i+1].innerText));
				ntr.appendChild(nth);
				const ntd = document.createElement('td');
				ntd.classList.add('bg-body');
				if (tr.children[i+1].classList.contains('text-secondary')) {
					ntd.classList.add('text-secondary');
				}
				ntd.style.maxWidth = '50vw';
				ntd.setAttribute('colspan', cols - 1);
				const npre = document.createElement('div');
				npre.classList.add('mb-0');
				npre.style.whiteSpace = 'pre';
				npre.classList.add('overflow-auto');
				npre.appendChild(document.createTextNode(tr.children[i+1].innerText));
				ntd.appendChild(npre);
				ntr.appendChild(ntd);
			}
			tr.parentNode.insertBefore(fragment, tr.nextSibling);
			const ntd = document.createElement('td');
			ntd.classList.add('bg-body');
			if (tr.children[1].classList.contains('text-secondary')) {
				ntd.classList.add('text-secondary');
			}
			ntd.style.maxWidth = '50vw';
			ntd.setAttribute('colspan', cols - 1);
			const npre = document.createElement('div');
			npre.classList.add('mb-0');
			npre.style.whiteSpace = 'pre';
			npre.classList.add('overflow-auto');
			ntd.appendChild(npre);
			npre.appendChild(document.createTextNode(tr.children[1].innerText));
			for (let i=cols-1; i>=0; i--) {
				tr.removeChild(tr.children[i+1]);
			}
			const nth = document.createElement('th');
			nth.classList.add('bg-body-tertiary');
			nth.appendChild(document.createTextNode(thead.children[1].innerText));
			tr.appendChild(nth);
			tr.appendChild(ntd);
			th.setAttribute('rowspan', cols);
		}
	}
}
